import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Topic from './Topic';
import TopicForm from './TopicForm';
import { fetchTopics, editTopic, createTopic, removeTopic, castVote, removeVote } from '../../store/topics';

const TopicList = () => {
    const dispatch = useDispatch();
    const topicsFromStore = useSelector(state => Object.values(state.topics));
    const [topics, setTopics] = useState(topicsFromStore);
    const [showForm, setShowForm] = useState(false);
    const [editingTopic, setEditingTopic] = useState(null);

    useEffect(() => {
      dispatch(fetchTopics())
          .then(data => {
              if (data) {
                  // Filter out any null topics
                  const validTopics = data.filter(topic => topic != null);
                  setTopics(validTopics);
              }
          })
          .catch(error => {
              console.error('Error fetching topics:', error);
          });
  }, [dispatch]);

    const handleCreateTopic = async (topicData) => {
              try {
                  const res = await dispatch(createTopic(topicData));
                  if (!res.errors) {
                      setShowForm(false);
                      dispatch(fetchTopics());
                  } else {
                      console.error('Failed to create topic:', res.errors);
                  }
              } catch (error) {
                  console.error('Error creating topic:', error);
              }
          };

          const handleEditTopic = async (topicData) => {
              try {
                  const res = await dispatch(editTopic(editingTopic.id, topicData));
                  if (!res.errors) {
                      setShowForm(false);
                      setEditingTopic(null);
                      dispatch(fetchTopics());
                  } else {
                      console.error('Failed to update topic:', res.errors);
                  }
              } catch (error) {
                  console.error('Error updating topic:', error);
              }
          };

          // const handleDeleteTopic = async (topicId) => {
          //     try {
          //         const response = await fetch(`/api/topics/${topicId}`, { method: 'DELETE' });
          //         if (!response.ok) throw new Error('Network response was not ok');
          //         dispatch(removeTopic(topicId));
          //     } catch (error) {
          //         console.error('Error deleting topic:', error);
          //     }
          // };

          const handleDeleteTopic = async (topicId) => {
            try {
              const response = await fetch(`/api/topics/${topicId}`, { method: 'DELETE' });
              if (!response.ok) throw new Error('Network response was not ok');
              dispatch(removeTopic(topicId));
              // Wait for state update before trying to re-render the topics list
              await dispatch(fetchTopics());
            } catch (error) {
              console.error('Error deleting topic:', error);
            }
          };



          const handleEditClick = (topic) => {
              setEditingTopic(topic);
              setShowForm(true);
          };

    const handleVote = async (topicId) => {
        // Dispatch the voting action
        const updatedTopic = await dispatch(castVote(topicId));
        if (updatedTopic) {
            // Update the local state with the new vote count
            const newTopics = topics.map(topic => 
                topic.id === topicId ? { ...topic, vote_count: topic.vote_count + 1 } : topic
            );
            setTopics(newTopics);
        }
    };

    const handleUnvote = async (topicId) => {
        // Dispatch the unvoting action
        const updatedTopic = await dispatch(removeVote(topicId));
        if (updatedTopic) {
            // Update the local state to decrement the vote count
            const newTopics = topics.map(topic => 
                topic.id === topicId ? { ...topic, vote_count: topic.vote_count - 1 } : topic
            );
            setTopics(newTopics);
        }
    };
    if (!topics || topics.length === 0) {
            return <p>Loading topics...</p>;
          }


          if (!Array.isArray(topics)) {
            console.error('Expected topics to be an array but got:', topics);
            return <p>Error: Topics data is not an array.</p>;
          }


          topics.map(topic => {
            if (!topic) {
              console.error('Null or undefined topic found', topic);
              return null; // or some placeholder component
            }
            const hasVoted = topic.hasVoted ?? false;
            return (
              <Topic
              key={topic.id}
              topic={topic}
              onEdit={handleEditClick}
              onDelete={handleDeleteTopic}
              onVote={() => handleVote(topic.id)}
              onUnvote={() => handleUnvote(topic.id)}
              hasVoted={hasVoted}
              />
            );
          })

    return (
        <div>
            <h1>Topics</h1>
            {/* Button and Form logic... */}
            <ul>
              {topics && topics.length > 0 && topics.map(topic => (
                <Topic
                  key={topic.id}
                  topic={topic}
                  onEdit={handleEditClick}
                  onDelete={handleDeleteTopic}
                  onVote={handleVote}
                  onUnvote={handleUnvote}
                  hasVoted={false} // Modify as needed
                />
              ))}
            </ul>
        </div>
    );
};

export default TopicList;
