//       <button onClick={() => onDelete(topic.id)} disabled={!userOwns}>Delete</button>

//       {!hasVoted ? (
//         <button onClick={() => onVote(topic.id)}>Vote</button>
//       ) : (
//         <button disabled>Vote</button>
//       )}

//       {voted && <button onClick={() => onUnvote(topic.id)}>Unvote</button>}
//     </div>
//   );
// }


const handleSave = () => {
  onSaveEdit(topic.id, { title: editTitle, description: editDescription });
  setIsEditing(false);
};

// Function to call when cancel button is clicked
const handleCancel = () => {
  onCancelEdit();
  setIsEditing(false);
};

// Render form fields if in edit mode, otherwise display the topic content
return (
  <div className="topic">
    {isEditing ? (
      <>
        <input value={editTitle} onChange={(e) => setEditTitle(e.target.value)} />
        <textarea value={editDescription} onChange={(e) => setEditDescription(e.target.value)} />
        <button onClick={handleSave}>Save</button>
        <button onClick={handleCancel}>Cancel</button>
      </>
    ) : (
      <>
        <h3>{topic.title}</h3>
        <p>{topic.description}</p>
        {/* ... rest of your non-edit content */}
        <button onClick={() => setIsEditing(true)} disabled={!userOwns}>Edit</button>
        {/* ... other buttons */}
      </>
    )}
  </div>
);
}

export default Topic;
