import React, { useState, useEffect } from 'react';
import Topic from './Topic';
import TopicForm from './TopicForm';

const TopicList = () => {
  const [topics, setTopics] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingTopic, setEditingTopic] = useState(null);

  useEffect(() => {
    fetchTopics();
  }, []);

  const fetchTopics = async () => {
    try {
      const response = await fetch('/api/topics');
      if (!response.ok) throw new Error('Network response was not ok');
      const data = await response.json();
      setTopics(data);
    } catch (error) {
      console.error('Error fetching topics:', error);
    }
  };

  const handleCreateOrUpdateTopic = async (topicData) => {
    const method = editingTopic ? 'PUT' : 'POST';
    const url = editingTopic ? `/api/topics/${editingTopic.id}` : '/api/topics';
    const token = localStorage.getItem('token'); // Retrieve token from local storage

    try {
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}` // Include the token in Authorization header
        },
        body: JSON.stringify(topicData),
      });
      if (!response.ok) throw new Error('Network response was not ok');

      const returnedTopic = await response.json();

      if (!editingTopic) {
        setTopics([...topics, returnedTopic]); // Add new topic to state
      } else {
        const updatedTopics = topics.map(topic => 
          topic.id === returnedTopic.id ? returnedTopic : topic
        );
        setTopics(updatedTopics); // Update topic in state
      }

      setShowForm(false);
      setEditingTopic(null);
    } catch (error) {
      console.error('Error submitting topic:', error);
    }
  };

  const handleDeleteTopic = async (topicId) => {
    const token = localStorage.getItem('token'); // Retrieve token from local storage

    try {
      const response = await fetch(`/api/topics/${topicId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}` // Include the token in Authorization header
        }
      });
      if (!response.ok) throw new Error('Network response was not ok');

      const remainingTopics = topics.filter(topic => topic.id !== topicId);
      setTopics(remainingTopics); // Update state to remove deleted topic
    } catch (error) {
      console.error('Error deleting topic:', error);
    }
  };

  const handleEditClick = (topic) => {
    setEditingTopic(topic);
    setShowForm(true);
  };

  return (
    <div>
      <h1>Topics</h1>
      <button onClick={() => setShowForm(true)}>Create New Topic</button>
      {showForm && (
        <TopicForm
          existingTopic={editingTopic}
          onSubmit={handleCreateOrUpdateTopic}
          onCancel={() => {
            setShowForm(false);
            setEditingTopic(null);
          }}
        />
      )}
      <ul>
        {topics.length > 0 ? (
          topics.map(topic => (
            <Topic
              key={topic.id}
              topic={topic}
              onEdit={handleEditClick}
              onDelete={handleDeleteTopic}
            />
          ))
        ) : (
          <p>Loading topics...</p>
        )}
      </ul>
    </div>
  );
};

export default TopicList;
