import React, { useState } from "react";
import { login } from "../../store/session";
import { useDispatch,useSelector } from "react-redux";
import { Redirect } from "react-router-dom";
import { useModal } from "../../context/Modal";
import { useHistory,Link } from 'react-router-dom';
import "./LoginForm.css";

function LoginFormModal() {
  const dispatch = useDispatch();
  const sessionUser = useSelector((state) => state.session.user);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errors, setErrors] = useState([]);
  const { closeModal } = useModal();
  const history = useHistory();
  successfulLogin - login;

  if (sessionUser) return <Redirect to="/" />;

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = await dispatch(login(email, password));
    if (data) {
      setErrors(data);
    } else {
        closeModal()
    }
  };

  const handleDemoLogin = (e) => {
    e.preventDefault(); // Prevent default form submission behavior
    setEmail('plato@socrates.com'); // Set demo credentials
    setPassword('demopassword');
    const data = dispatch(login('plato@socrates.com', 'demopassword'));
    if (data) {
      setErrors(data);
    }
    else if (successfulLogin) {
      closeModal(); // Ensure this is called after the login process is complete
  } 
    else {
      // Clear the form fields after successful login
      closeModal()
    }
  };

  return (
    <>
      <h1>Log In</h1>
      <form onSubmit={handleSubmit}>
        <ul>
        {Array.isArray(errors) && errors.map((error, idx) => (
    <li key={idx}>{error}</li>
          ))}
        </ul>
        <label>
          Email
          <input
            type="text"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </label>
        <label>
          Password
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </label>
        <button type="submit">Log In</button>
        <button onClick={handleDemoLogin}>Log in as Demo Member</button>
      </form>
    </>
  );
}

export default LoginFormModal;
