import React, { useState } from "react";
import { useSelector } from "react-redux";

function Topic({ topic, onEdit, onSaveEdit, onCancelEdit, onDelete, onVote, onUnvote, userOwns, isEditing }) {
  const [editTitle, setEditTitle] = useState(topic.title);
  const [editDescription, setEditDescription] = useState(topic.description);
  const user = useSelector((state) => state.session.user);
  const hasVoted = useSelector((state) => state?.topics?.hasVoted);

  if (!topic) {
    // Optionally, render nothing or a placeholder
    return null; // or <div>Topic not found</div>
  }

  // Handle the inline edit view
  if (isEditing) {
    return (
      <div className="topic-edit">
        <input
          type="text"
          value={editTitle}
          onChange={(e) => setEditTitle(e.target.value)}
        />
        <textarea
          value={editDescription}
          onChange={(e) => setEditDescription(e.target.value)}
        />
        <button onClick={() => onSaveEdit(topic.id, { title: editTitle, description: editDescription })}>Save</button>
        <button onClick={() => onCancelEdit()}>Cancel</button>
      </div>
    );
  }

  // If not editing, display the topic normally
  const createdAt = new Date(topic?.created_at)?.toLocaleString();
  const updatedAt = new Date(topic?.updated_at)?.toLocaleString();
  const voteCount = topic?.vote_count || 0;

  return (
    <div className="topic">
      <h3>{topic.title}</h3>
      <p>{topic.description}</p>
      <small>Created at: {createdAt}</small>
      <br />
      <small>Last updated: {updatedAt}</small>
      <p>Votes: {voteCount}</p>
      <button onClick={() => onEdit(topic)} disabled={!userOwns}>Edit</button>
      <button onClick={() => onDelete(topic.id)} disabled={!userOwns}>Delete</button>

      {!hasVoted ? (
        <button onClick={() => onVote(topic.id)}>Vote</button>
      ) : (
        <button disabled>Vote</button>
      )}

      {user && topic?.votes?.some((vote) => vote?.user_id === user.id) && (
        <button onClick={() => onUnvote(topic.id)}>Unvote</button>
      )}
    </div>
  );
}

export default Topic;
