// store/topics.js

const LOAD_TOPICS = 'topics/LOAD_TOPICS';

const loadTopics = (topics) => ({
  type: LOAD_TOPICS,
  topics,
});

export const fetchTopics = () => async (dispatch) => {
  const response = await fetch('/api/topics');
  if (response.ok) {
    const topics = await response.json();
    dispatch(loadTopics(topics));
  }
};


const createTopic = async (newTopicData) => {
    // ... same as before
    const newTopic = await response.json();
    dispatch(addTopic(newTopic)); // Dispatch action to update Redux store
  };

const initialState = [];

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case LOAD_TOPICS:
      return [...action.topics];
    default:
      return state;
  }
}
