import React, { useState, useEffect } from 'react';
import Topic from './Topic';


const TopicList = () => {
    const [topics, setTopics] = useState([]);

    useEffect(() => {
      const fetchTopics = async () => {
        try {
          const response = await fetch('/api/topics'); // Adjust the URL based on your API endpoint
          if (!response.ok) throw new Error('Network response was not ok');
          const data = await response.json();
          setTopics(data.topics); // Make sure the key matches what your backend sends
        } catch (error) {
          console.error('Error fetching topics:', error);
        }
      };

      fetchTopics();
    }, []);

    return (
        <div>
          <h1>Topics</h1>
          <ul>
            {topics.length > 0 ? (
              topics.map(topic => (
                <li key={topic.id}>{topic.title}</li> // Adjust based on your data structure
              ))
            ) : (
              <p>Loading topics...</p>
            )}
          </ul>
        </div>
      );

            };


export default TopicList;
